import React, { Component } from "react";
import { View, SafeAreaView, FlatList, ScrollView, Text } from "react-native";
import { CustomHeader } from "../../index";
import { Avatar, Button, Card, Title, Paragraph } from "react-native-paper";
import axios from "axios";

export class NewsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      detail: [],
    };
  }
  componentDidMount() {
    axios
      .get("http://localhost:9000/news", "desc")
      .then((response) =>
        this.setState({
          detail: response.data,
        })
      )
      .catch((er) => console.log(er.messagge));
  }

  renderDetail = () => {
    return this.state.detail.map((map) => {
      for (var i = 0; i < this.state.detail.length; i++) {
        if (map.id === this.state.detail[i].id) {
          console.log(i);
          return (
            <View style={{marginBottom:10}}>
              {/* <Text>{this.state.detail[i].title}</Text>
              <Text>{this.state.detail[i].description}</Text> */}
              <Card>
                <Card.Title
                  title={this.state.detail[i].title}
                  subtitle= {this.state.detail[i].date}
                />
                <Card.Content>
                  <Paragraph>{this.state.detail[i].description}</Paragraph>
                </Card.Content>
                {/* <Card.Cover source={{ uri: "https://picsum.photos/700" }} /> */}
              </Card>
            </View>
          );
        }
      }
    });
  };
  render() {
    return (
      <SafeAreaView style={{ flex: 1, backgroundColor: "#39D5D5" }}>
        <CustomHeader
          title="NEWS"
          isHome={true}
          navigation={this.props.navigation}
        />
        <View style={{ flex: 1, margin: 20 }}>
          <ScrollView>{this.renderDetail()}</ScrollView>
        </View>
      </SafeAreaView>
    );
  }
}
