import React, { Component } from "react";
import { SafeAreaView, View, Text } from "react-native";
import { CustomHeader } from "../../index";
import axios from "axios";
import {
  Avatar,
  Button,
  Card,
  Title,
  Paragraph,
  TextInput,
  Badge,
} from "react-native-paper";
export class ReportScreen extends Component {
  // construct
  state = {
    // fullname: "",
    // email: "",
    // tel: "",
    description: "",
  };
  // constructor(props){
  //   super(props);
  //     this.state={
  //       description: "",
  //     }
  //     // this.handleChange = this.handleChange.bind(this);
  //   }


  handleChange (event) {
    // this.setState({ fullname: event.target.value });
    // this.setState({ email: event.target.value });
    // this.setState({ tel: event.target.value });
    this.setState({ description: event.target.value });
  };

  handleSubmit (event) {
    event.preventDefault();

    const problem = {
      // fullname: this.state.fullname,
      // email: this.state.email,
      // tel: this.state.tel,
      description: this.state.description,
    };

    axios.post("http://localhost:9000/user", { problem }).then((res) => {
    // res.data.fullname,
      console.log(res);
      console.log(res.data);
    });
  };
  render() {
    return (
      <SafeAreaView style={{ flex: 1, backgroundColor: "#39D5D5" }}>
        <CustomHeader title="REPORT" navigation={this.props.navigation} />
        <View style={{ margin: 20 }}>
          <Card style={{ marginBottom: 10, height: 80 }}>
            <Card.Title
              title="Report Problem"
              subtitle="แจ้งปัญหาหรือสามารถสอบถามได้ที่นี่"
            />

            <Card>
              <Card.Content>
                {/* <TextInput
                  style={{ marginBottom: 10, height: 60, borderRadius:10 }}
                  label="Full Name"
                  placeholder="Somchai Rukmee"
                  mode="outline"
                  name="fullname"
                  value={this.state.fullname}
                  onChange={this.handleChange}
                  // onChange={(e) => this.setState.fullname(e.target.value)}
                />
                <TextInput
                  style={{ marginBottom: 10, height: 60, borderRadius:10 }}
                  label="Email"
                  placeholder="somchai@hotmil.com"
                  name="email"
                  value={this.state.email}
                  onChange={this.handleChange}
                />
                <TextInput
                  style={{ marginBottom: 10, height: 60 , borderRadius:10}}
                  label="Tel"
                  placeholder="08x-xxx-xxxx"
                  name="tel"
                  value={this.state.tel}
                  onChange={this.handleChange}
                /> */}
                <TextInput
                  style={{ marginBottom: 10, height: 80 , borderRadius:10}}
                  multiline={true}
                  label="Description"
                  // name="description"
                  value={this.state.description}
                  onChangeText={this.handleChange}
                />
                <Button color='orange' dark={true} mode="contained" onPress={()=>this.handleSubmit}>
                  OK
                </Button>
              </Card.Content>
            </Card>
          </Card>
        </View>
      </SafeAreaView>
    );
  }
}
