        const ref = firestore.collection('news');
        ref.onSnapshot(
            (snapshot) => {
                let tempDataArray = [];
                snapshot.forEach((doc) =>{
                    if (doc.exists){
                        tempDataArray = [
                            ...tempDataArray,
                            {
                                id: doc.id,
                                title : doc.data().title,
                                description : doc.data().description,
                            },
                        ];
                    }
                });
                this.setState((oldDataArray) => tempDataArray);
            },
            (err) => {
                console.log(err)
            },[]
        );
    }
    render() {
      return (
        <SafeAreaView style={{ flex: 1, backgroundColor: "#39D5D5" }}>
          <CustomHeader
            title="NEWS"
            isHome={true}
            navigation={this.props.navigation}
          />
          <View style={{ flex: 1, alignItems: "center" }}>
          <FlatList
                data={this.state.data1}
                renderItem={this.renderItem}
              />
          </View>
        </SafeAreaView>
      );
    }
}
