import React, { Component } from "react";
import { SafeAreaView, View, Text } from "react-native";
import { CustomHeader } from "../../index";
import axios from "axios";
import {
  Avatar,
  Button,
  Card,
  Title,
  Paragraph,
  TextInput,
  Badge,
} from "react-native-paper";
export class ReportScreen extends Component {
  state = {
    fullname: "",
    email: "",
    tel: "",
    description: "",
  };

  handleChange = (event) => {
    this.setState({
      fullname: event.target.value,
      email: event.target.value,
      tel: event.target.value,
      description: event.target.value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    const user = {
      fullname: this.state.fullname,
      email: this.state.email,
      tel: this.state.tel,
      description: this.state.description,
    };

    axios.post("http://localhost:9000/user", { user }).then((res) => {
      console.log(res);
      console.log(res.data);
    });
  };
  render() {
    return (
      <SafeAreaView style={{ flex: 1, backgroundColor: "#39D5D5" }}>
        <CustomHeader title="REPORT" navigation={this.props.navigation} />
        <View style={{ margin: 20 }}>
          <Card style={{ marginBottom: 10, height: 80 }}>
            <Card.Title
              title="Report Problem"
              subtitle="แจ้งปัญหาหรือสามารถสอบถามได้ที่นี่"
            />

            <Card>
              <Card.Content>
                <TextInput
                  style={{ marginBottom: 10, height: 60 }}
                  label="Full Name"
                  placeholder="Somchai Rukmee"
                  mode="outline"
                  name="fullname"
                  value={this.state.fullname}
                  onChange={this.handleChange}
                />
                <TextInput
                  style={{ marginBottom: 10, height: 60 }}
                  label="Email"
                  placeholder="somchai@hotmil.com"
                  name="email"
                  value={email}
                  onChange={this.handleChange}
                />
                <TextInput
                  style={{ marginBottom: 10, height: 60 }}
                  label="Tel"
                  placeholder="08x-xxx-xxxx"
                  name="tel"
                  value={tel}
                  onChange={this.handleChange}
                />
                <TextInput
                  style={{ marginBottom: 10, height: 80 }}
                  multiline={true}
                  label="Description"
                  name="description"
                  value={description}
                  onChange={this.handleChange}
                />
                <Button mode="contained" onPress={this.handleSubmit}>
                  OK
                </Button>
              </Card.Content>
            </Card>
          </Card>
        </View>
      </SafeAreaView>
    );
  }
}
